import React, { useContext } from 'react'
import './FoodDisplay.css'
import { StoreContext } from '../context/StoreContext'
import FoodItem from '../FoodItem/FoodItem'

const FoodDisplay = ({ category }) => { // Destructure the props object properly

    const { food_list } = useContext(StoreContext)
    return (
        <div className='food-display' id='food-display'>
            <h2>Top Dishes near you</h2>
            <div className="food-display-list">
                {food_list.map((item, index) => {
                    console.log(category, item.category);
                    if (category === 'All' || category === item.category) {
                        return <FoodItem key={index} id={item.id} name={item.name} description={item.description} price={item.price} image={item.image} />
                    }
                    return null; // Ensure you always return something from map function
                })}
            </div>
        </div>
    )
}

export default FoodDisplay
