import React, { useState, useContext, useEffect } from "react";
import "./FoodItem.css";
import { assets } from "../../assets/assets";
import { StoreContext } from "../context/StoreContext";

const FoodItem = ({ id, name, price, description, image }) => {
  const { addToCart, removeFromCart, cartItems } = useContext(StoreContext);
  const [cartItemCount, setCartItemCount] = useState(0);
  const [isAdding, setIsAdding] = useState(false); // Flag for adding state

  useEffect(() => {
    const quantity = cartItems[id] || 0;
    setCartItemCount(quantity);
  }, [cartItems, id]);

  const handleAddToCart = () => {
    setIsAdding(true); // Set flag before adding
    addToCart(id);
    setIsAdding(false); // Reset flag after adding
  };

  const handleRemoveFromCart = () => {
    if (cartItemCount > 0) {
      removeFromCart(id);
    }
  };

  return (
    <div className="food-item-img-container">
      <img className="food-item-image" src={image} alt={name} />
      {!cartItemCount || cartItems[id] <= 0 ? (
        <img
          className="add"
          onClick={handleAddToCart}
          src={assets.add_icon_white}
          alt="Add"
        />
      ) : isAdding ? ( // Only show counter if adding
        <div className="food-item-counter">
          <img
            onClick={handleRemoveFromCart}
            src={assets.remove_icon_red}
            alt="Remove"
          />
          <p>{cartItemCount}</p>
          <img
            onClick={handleAddToCart}
            src={assets.add_icon_green}
            alt="Add"
          />
        </div>
      ) : (
        // Show "Remove" button only if item exists
        <img
          onClick={handleRemoveFromCart}
          src={assets.remove_icon_red}
          alt="Remove"
        />
      )}
      <div className="food-item-info">
        {/* ... rest of the component */}
      </div>
    </div>
  );
};

export default FoodItem;
